mapper.select:
SELECT 
	t1.id,t1.order_no,t1.create_time,t1.clean_time,(case when t2.clean_amount is null then 0 else t2.clean_amount end) clean_amount
FROM stake_order_h t1
LEFT JOIN order_clean t2 ON t1.id=t2.id

	AND t1.order_no = #{orderNo,jdbcType=CHAR}
	AND t1.customer_id = #{customerId,jdbcType=INTEGER}
	AND t1.customer_name = #{customerName,jdbcType=VARCHAR}
	AND t1.broker_id = #{brokerId,jdbcType=INTEGER}
	AND t1.broker_name = #{brokerName,jdbcType=VARCHAR}
	AND t1.member_id = #{memberId,jdbcType=INTEGER}
	AND t1.member_name = #{memberName,jdbcType=VARCHAR}
	AND t1.schedule_id = #{scheduleId,jdbcType=INTEGER}
	AND t1.stake_type = #{stakeType,jdbcType=CHAR}
	AND t1.stake_code = #{stakeCode,jdbcType=CHAR}
	AND t1.stake_price = #{stakePrice,jdbcType=DECIMAL}
	AND t1.order_num = #{orderNum,jdbcType=DECIMAL}
	AND t1.trade_fee = #{tradeFee,jdbcType=DECIMAL}
	AND t1.member_fee = #{memberFee,jdbcType=DECIMAL}
	AND t1.stake_amount = #{stakeAmount,jdbcType=DECIMAL}
	AND t1.pay_type = #{payType,jdbcType=CHAR}
	AND t1.order_status = #{orderStatus,jdbcType=CHAR}
	AND t1.create_time = #{createTime,jdbcType=TIMESTAMP}
	AND t1.is_clean = #{isClean,jdbcType=CHAR}		



		ORDER BY ${field} ${sort}

	ORDER BY t1.id DESC


	LIMIT ${offset},${limit}

SELECT count(*)
FROM stake_order_H

	AND order_no = #{orderNo,jdbcType=CHAR}
	AND customer_id = #{customerId,jdbcType=INTEGER}
	AND customer_name = #{customerName,jdbcType=VARCHAR}
	AND broker_id = #{brokerId,jdbcType=INTEGER}
	AND broker_name = #{brokerName,jdbcType=VARCHAR}
	AND member_id = #{memberId,jdbcType=INTEGER}
	AND member_name = #{memberName,jdbcType=VARCHAR}
	AND schedule_id = #{scheduleId,jdbcType=INTEGER}
	AND stake_type = #{stakeType,jdbcType=CHAR}
	AND stake_code = #{stakeCode,jdbcType=CHAR}
	AND stake_price = #{stakePrice,jdbcType=DECIMAL}
	AND order_num = #{orderNum,jdbcType=DECIMAL}
	AND trade_fee = #{tradeFee,jdbcType=DECIMAL}
	AND member_fee = #{memberFee,jdbcType=DECIMAL}
	AND stake_amount = #{stakeAmount,jdbcType=DECIMAL}
	AND pay_type = #{payType,jdbcType=CHAR}
	AND order_status = #{orderStatus,jdbcType=CHAR}
	AND create_time = #{createTime,jdbcType=TIMESTAMP}
	AND is_clean = #{isClean,jdbcType=CHAR}
