mapper.select:
 	select 

	from broker

    AND member_id = #{memberId,jdbcType=INTEGER}
    AND broker_code = #{brokerCode,jdbcType=VARCHAR}
    AND sign_code = #{signCode,jdbcType=VARCHAR}
    AND level_num = #{levelNum,jdbcType=TINYINT}
    AND level_code like CONCAT(#{levelCode,jdbcType=VARCHAR},'%')
    AND login_name = #{loginName,jdbcType=VARCHAR}
    AND STATUS = #{status,jdbcType=CHAR}



			ORDER BY ${field} ${sort}


			order by broker_id



		LIMIT ${offset}, ${limit}

 	select count(*)
	from broker

    AND member_id = #{memberId,jdbcType=INTEGER}
    AND broker_code = #{brokerCode,jdbcType=VARCHAR}
    AND sign_code = #{signCode,jdbcType=VARCHAR}
    AND level_num = #{levelNum,jdbcType=TINYINT}
    AND level_code like CONCAT(#{levelCode,jdbcType=VARCHAR},'%')
    AND login_name = #{loginName,jdbcType=VARCHAR}
    AND STATUS = #{status,jdbcType=CHAR}



			ORDER BY ${field} ${sort}


			order by broker_id



		LIMIT ${index}, ${row}
